.watermark {
    position: fixed; /* Use fixed to keep it centered even when scrolling */
    top: 50%; /* Position the top edge of the element at the middle of the screen */
    left: 50%; /* Position the left edge of the element at the middle of the screen */
    width: 100%; /* Fixed width */
    height: 100%; /* Fixed height */
    background-image: url('/assets/nxpo_hrms/images/ssl/bg1.png'); /* Path to your watermark image */
    background-position: center; /* Ensure the background image is centered within the element */
    background-repeat: no-repeat; /* No repeating of the background */
    opacity: 0.1; /* Semi-transparent */
    z-index: 1; /* High z-index to ensure it's above other content */
    transform: translate(-50%, -50%); /* Shift the element back by its own half width and height to truly center it */
}

.logo {
    /*position: absolute;*/
    /*top: 50;  */
    /*right: 50; */
    height: 50px; 
    width: auto;          
}


@media screen {
	.print-format-gutter {
		background-color: #d1d8dd;
		padding: 30px 0px;
	}
	.print-format {
		background-color: white;
		border-radius: 8px;
		max-width: 8.3in;
		min-height: 11.69in;
		padding: 0.25in;
		margin: auto;
		color: var(--gray-900);
	}

	.print-format.landscape {
		max-width: 11.69in;
		padding: 0.2in;
	}

	.page-break {
		/* padding: 15px 0px; */
		border-bottom: 1px dashed #888;
	}

	/* .page-break:first-child {
		padding-top: 0px;
	} */

	.page-break:last-child {
		border-bottom: 0px;
	}

	/* mozilla hack for images in table */
	body:last-child .print-format td img {
		width: 100% !important;
	}

	@media(max-width: 767px) {
		.print-format {
			padding: 0.2in;
		}
	}
}

@media print {
	.print-format p {
		margin-left: 1px;
		margin-right: 1px;
	}
}

