html:
{% set dateConvert = doc.posting_date|string %}
{% set date_parts = dateConvert.split('-') %}
{% set year = date_parts[0]%}
{% set month = date_parts[1] %}
{% set day = date_parts[2] | int %}
{% set month_th = {
      '01': 'มกราคม', '02': 'กุมภาพันธ์', '03': 'มีนาคม', '04': 'เมษายน',
      '05': 'พฤษภาคม', '06': 'มิถุนายน', '07': 'กรกฎาคม', '08': 'สิงหาคม',
      '09': 'กันยายน', '10': 'ตุลาคม', '11': 'พฤศจิกายน', '12': 'ธันวาคม'
}[month] %}

{% set earnings = 
                [
                    "เงินเดือน",
                    "ประโยชน์ตอบแทนอื่น",
                    "เงินเพิ่มพิเศษ",
                    "ตกเบิก", 
                    "เงินได้อื่นๆ ก่อนภาษี",
                    "เงินชดเชย 1",
                    "เงินชดเชย 2"
                ]  
%}

{% set deductions = 
                [
                    "ภาษี", 
                    "ภาษีเงินชดเชย",
                    "กองทุนสำรองเลี้ยงชีพ",
                    "สหกรณ์ออมทรัพย์ วท.",
                    "ธนาคารอาคารสงเคราะห์",
                    "กยศ.", 
                    "เงินหักอื่นๆ"
                ]  
%}

{% set sum_earnings = namespace(result=0) %}
{% set sum_deductions = namespace(result=0) %}

{% set conunt_pvd_emp = namespace(result=0) %}
{% set conunt_pvd_com = namespace(result=0) %}

{% set sum1 = namespace(result=0) %}
{% set sum4 = namespace(result=0) %}
{% set sum5 = namespace(result=0) %}
{% set sum6 = namespace(result=0) %}
{% set sum7 = namespace(result=0) %}

<div class="page-break">
    <div class="row section-break" data-label="Header">
        <div class="col-xs-12 column-break">
            <div class="row  data-field">
                <div class="col-xs-6 text-left">
                    <img class="logo" src="/assets/nxpo_hrms/images/ssl/logo1.png" alt="Organization Logo" />
                </div>
                <div class="col-xs-6 text-right">
                    <label style="color: black; font-size:x-small"> 
                        Printed On
                            {{ frappe.format(frappe.utils.now_datetime(), {'fieldtype': 'Datetime'}) }}
                    </label>
                </div>
            </div>
        </div>
        <div class="col-xs-12 column-break">
            <p style="color: black; text-align: center; font-weight: bold; ">
                สำนักงานสภานโยบายการอุดมศึกษา วิทยาศาสตร์ วิจัยและนวัตกรรมแห่งชาติ
            </p>
            <p style="color: black; text-align: center;  ">
                รายงานสรุปจ่ายเงินเดือน เดือน {{month_th}} {{year}} ( วันที่จ่าย {{day}}/{{month_th}}/{{year}} )
            </p>
        </div>
    </div>
	<div class="dbt">
		<hr class="bt">
	</div>
    <div class="row section-break" data-label="Detail">
        <div class="col-xs-12 column-break">
            <label style="color: black; text-align: left;"><b>รายละเอียดการจ่าย</b></label>
            <div class="earnings-detail" data-label="earnings">
                <div class="row  data-field" style="padding: 0px 5px">
                    {% for earning in earnings %}
                        {% set value = sum_amount_ss_component(doc.company, doc.start_date, doc.end_date, earning) %}
                        {% set sum_earnings.result = sum_earnings.result + value %}
                        {% if value %}
                            <div class="col-xs-12 my-1" >
                                <div class="row data-field" >
                                    <div class="col-xs-6">
                                        <label style="color: black; text-align: left; ">{{ earning }}</label>
                                    </div>
                                    <div class="col-xs-4">
                                        <label style="color: black; text-align: right; "> 
                                            {{ "{:,.2f}".format(value | default(0, true)) }} 
                                        </label>
                                    </div>
                                    <div class="col-xs-2">
                                        <label style="color: black; text-align: left; padding-left: 5px;">บาท</label>
                                    </div>
                                </div>
                            </div>
                        {% endif %}
                    {% endfor %}
                </div>
                <div class="row data-field" style="padding: 0px 5px">
                    <div class="col-xs-12 my-1">
                        <div class="row data-field" >
                            <div class="col-xs-6">
                                <label style="color: black; text-align: left;">รวม</label>
                            </div>
                            <div class="col-xs-4">
                                <label style="color: black; text-align: right; border-top: solid 1px black; border-bottom: solid 1px black;">
                                   <strong>  {{ "{:,.2f}".format(sum_earnings.result | default(0, true)) }}</strong>
                                </label>
                            </div>
                            <div class="col-xs-2">
                                <label style="color: black; text-align: left; padding-left: 5px;">บาท</label>
                            </div>
                        </div>
                    </div>
                </div>


            </div>
            <label style="color: black; text-align: left;"><b>รายละเอียดหักพนักงาน</b></label>
            <div class="deductions-detail" data-label="deductions">
                <div class="row  data-field" style="padding: 0px 5px">
                    {% for deduction in deductions %}

                        {% set value = sum_amount_ss_component(doc.company, doc.start_date, doc.end_date, deduction) %}


                        {% set sum_deductions.result = sum_deductions.result + value %}
                        {% if deduction == 'สหกรณ์ออมทรัพย์ วท.' %}
                            {% set sum4.result = value %}
                        {% elif deduction == 'ธนาคารอาคารสงเคราะห์' %}
                            {% set sum5.result = value %}
                        {% elif deduction == 'กยศ.' %}
                            {% set sum6.result = value %}
                        {% elif deduction == 'ภาษี' %}
                            {% set sum7.result = value %}
                        {% endif %}
                        {% if value %}
                            <div class="col-xs-12 my-1" >
                                <div class="row data-field">
                                    <div class="col-xs-6">
                                        <label style="color: black; text-align: left; ">{{ deduction }}</label>
                                    </div>
                                    <div class="col-xs-4">
                                        <label style="color: black; text-align: right;"> 
                                            {{ "{:,.2f}".format(value | default(0, true)) }} 
                                        </label>
                                    </div>
                                    <div class="col-xs-2">
                                        <label style="color: black; text-align: left; padding-left: 5px;">บาท</label>
                                    </div>
                                </div>
                            </div>
                        {% endif %}
                    {% endfor %}
                </div>
                <div class="row data-field" style="padding: 0px 5px">
                    <div class="col-xs-12 my-1">
                        <div class="row data-field" >
                            <div class="col-xs-6">
                                <label style="color: black; text-align: left;">รวม</label>
                            </div>
                            <div class="col-xs-4">
                                <label style="color: black; text-align: right; border-top: solid 1px black; border-bottom: solid 1px black;">
                                  <strong>  {{ "{:,.2f}".format(sum_deductions.result | default(0, true)) }} </strong>
                                </label>
                            </div>
                            <div class="col-xs-2">
                                <label style="color: black; text-align: left;  padding-left: 5px; ">บาท</label>
                            </div>
                        </div>
                    </div>
                </div>

            </div>

            <div class="deductions-detail" data-label="sum">
                <div class="row data-field" >
                    <div class="col-xs-6">
                        <label style="color: black; text-align: left; padding-left: 5px;"><strong>รวมจ่ายพนักงานเข้าธนาคาร</strong></label>
                    </div>
                    <div class="col-xs-4">
                        {% set sum =  sum_earnings.result - sum_deductions.result%}
                        {% set sum1.result = sum %}
                        <label style="color: black; text-align: right; padding-right: 5px; border-bottom: solid 1px black;">
                          <strong>  {{ "{:,.2f}".format(sum | default(0, true)) }}</strong> 
                        </label>
                    </div>
                    <div class="col-xs-2">
                        <label style="color: black; text-align: left; padding-left: 2px;">บาท</label>
                    </div>
                </div>
            </div>
        </div>
    </div>

	<div class="text-right my-1" data-label="count-emps">
	   <label style="color: black;">จำนวนพนักงาน {{doc.number_of_employees}} คน</label>
	</div>
	<div class="dbt">
		<hr class="bt1">
	</div>
	<label class="text-left mt-2" style="color: black"> <strong>สรุปการจ่ายกองทุนสำรองเลี้ยงชีพตามแผนการลงทุน </strong></label>
	<div class="row section-break" data-label="table">
	    <div class="col-xs-12 column-break">

            <div data-fieldname="pvd" data-fieldtype="Table" style="padding: 0px 50px">
            	<table class="table table-bordered table-condensed">
            	    <thead>
            			<tr>
            				<th style="width: 40%; " class="" data-fieldname="pvd" data-fieldtype="Table">
            				    <strong>
            					    นโยบาย
            					</strong>
            				</th>
            				<th style="width: 20%; " class="text-right" data-fieldname="pvd" data-fieldtype="Table">
            				    <strong>
            						สะสม
            				    </strong>
            				</th>
            				<th style="width: 20%; " class="text-right" data-fieldname="pvd" data-fieldtype="Table">
            				    <strong>
            						สมทบ
            				    </strong>
            				</th>
            				<th style="width: 20%; " class="text-right" data-fieldname="pvd" data-fieldtype="Table">
            				    <strong>
            						รวม
            				    </strong>
            				</th>
            			</tr>
            	    </thead>

            		<tbody>

            		    {% set sum_pvd1 = namespace(result=0) %}
            		    {% set sum_pvd2 = namespace(result=0) %}
            		    {% set sum_pvd3 = namespace(result=0) %}
            		    {% set sum_pvd4 = namespace(result=0) %}
            		    {% set sum_pvd_com1 = namespace(result=0) %}
            		    {% set sum_pvd_com2 = namespace(result=0) %}
            		    {% set sum_pvd_com3 = namespace(result=0) %}
            		    {% set sum_pvd_com4 = namespace(result=0) %}

            		    {% set pvdTypeData = frappe.get_all('PVD Type', fields=['name']) %}
            		    {% for pvdType in pvdTypeData %}


            		        {% set filters = [
            		            ['custom_pvd_type', '=', pvdType.name],
                                ['start_date', '>=', doc.start_date],
                                ['end_date', '<=', doc.end_date],
                                ['company', '=', doc.company],
                                ['docstatus', 'in', [0, 1]]
                            ] %}
                            {% set slData = frappe.get_all('Salary Slip', filters=filters, fields=['name']) %}

                            <tr>

                                <td class="" data-fieldname="pvd" data-fieldtype="Table">
        				          	<div class="value">{{pvdType.name}}</div>
        				        </td>

        				        {% for sl in slData %}
                                    {% set valuePvdEmp1 = frappe.db.get_value('Salary Detail', { 'parent': sl.name, 'salary_component': 'กองทุนสำรองเลี้ยงชีพ'}, 'amount') %}
                                    {% set valuePvdCom = frappe.db.get_value('Salary Detail', { 'parent': sl.name, 'salary_component': 'กองทุนสำรองเลี้ยงชีพสำนักงานสมทบ'}, 'amount') %}

                                    {% if pvdType.name == 'กองทุนสำรองเลี้ยงชีพ (ผสมหุ้นไม่เกิน 25%)' %}
                                        {% set sum_pvd1.result = sum_pvd1.result|float + valuePvdEmp1|float %}
                                        {% set sum_pvd_com1.result = sum_pvd_com1.result|float + valuePvdCom|float %}
                                    {% endif %}

                                    {% if pvdType.name == 'กองทุนสำรองเลี้ยงชีพ (ผสมหุ้นไม่เกิน 10%)' %}
                                        {% set sum_pvd2.result = sum_pvd2.result|float + valuePvdEmp1|float %}
                                        {% set sum_pvd_com2.result = sum_pvd_com2.result|float + valuePvdCom|float %}
                                    {% endif %}

                                    {% if pvdType.name == 'กองทุนสำรองเลี้ยงชีพ (ตราสารหนี้)' %}
                                        {% set sum_pvd3.result = sum_pvd3.result|float + valuePvdEmp1|float %}
                                        {% set sum_pvd_com3.result = sum_pvd_com3.result|float + valuePvdCom|float %}
                                    {% endif %}

                                    {% if pvdType.name == 'กองทุนสำรองเลี้ยงชีพ (DIY)' %}
                                        {% set sum_pvd4.result = sum_pvd4.result|float + valuePvdEmp1|float %}
                                        {% set sum_pvd_com4.result = sum_pvd_com4.result|float + valuePvdCom|float %}
                                    {% endif %}


        				        {% endfor %}


        				        <td class="text-right" data-fieldname="pvd" data-fieldtype="Table">
        				          	<div class="value">

            				            {% if pvdType.name == 'กองทุนสำรองเลี้ยงชีพ (ผสมหุ้นไม่เกิน 25%)' %}
                                             {{ "{:,.2f}".format(sum_pvd1.result | default(0, true)) }}
                                        {% endif %}

                                        {% if pvdType.name == 'กองทุนสำรองเลี้ยงชีพ (ผสมหุ้นไม่เกิน 10%)' %}
                                             {{ "{:,.2f}".format(sum_pvd2.result | default(0, true)) }}
                                        {% endif %}

                                        {% if pvdType.name == 'กองทุนสำรองเลี้ยงชีพ (ตราสารหนี้)' %}
                                             {{ "{:,.2f}".format(sum_pvd3.result | default(0, true)) }}
                                        {% endif %}

                                        {% if pvdType.name == 'กองทุนสำรองเลี้ยงชีพ (DIY)' %}
                                             {{ "{:,.2f}".format(sum_pvd4.result | default(0, true)) }}
                                        {% endif %}
        				          	</div>
        				        </td>

        				        <td class="text-right" data-fieldname="pvd" data-fieldtype="Table">
        				          	<div class="value">
        				          	    {% if pvdType.name == 'กองทุนสำรองเลี้ยงชีพ (ผสมหุ้นไม่เกิน 25%)' %}
                                             {{ "{:,.2f}".format(sum_pvd_com1.result | default(0, true)) }}
                                        {% endif %}

                                        {% if pvdType.name == 'กองทุนสำรองเลี้ยงชีพ (ผสมหุ้นไม่เกิน 10%)' %}
                                             {{ "{:,.2f}".format(sum_pvd_com2.result | default(0, true)) }}
                                        {% endif %}

                                        {% if pvdType.name == 'กองทุนสำรองเลี้ยงชีพ (ตราสารหนี้)' %}
                                             {{ "{:,.2f}".format(sum_pvd_com3.result | default(0, true)) }}
                                        {% endif %}

                                        {% if pvdType.name == 'กองทุนสำรองเลี้ยงชีพ (DIY)' %}
                                             {{ "{:,.2f}".format(sum_pvd_com4.result | default(0, true)) }}
                                        {% endif %}
        				          	</div>
        				        </td>

        				        <td class="text-right" data-fieldname="pvd" data-fieldtype="Table">
        				          	<div class="value">
        				          	    {% if pvdType.name == 'กองทุนสำรองเลี้ยงชีพ (ผสมหุ้นไม่เกิน 25%)' %}
                                             {{ "{:,.2f}".format(sum_pvd1.result | default(0, true) + sum_pvd_com1.result | default(0, true)) }}
